components/common/currency-selector:
{{#if currency_selector.currencies.length '>' 1}}
<ul class="navUser-section">
    <li class="navUser-item">
        <a class="navUser-action has-dropdown" href="#" data-dropdown="currencySelection" aria-controls="currencySelection" aria-expanded="false">{{lang 'common.currency' code=currency_selector.active_currency_code}} <i class="icon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i></a>
        <ul class="dropdown-menu" id="currencySelection" data-dropdown-content aria-hidden="true" tabindex="-1">
            {{#each currency_selector.currencies}}
            <li class="dropdown-menu-item">
                <a href="{{{switch_url}}}">
                    {{#if is_active}}
                    <strong>{{name}}</strong>
                    {{else}}
                    {{name}}
                    {{/if}}
                </a>
            </li>
            {{/each}}
        </ul>
    </li>
</ul>
{{/if}}

components/common/navigation-list-altern:
{{#if children}}
    <a class="navPages-action navPages-action-depth-max has-subMenu is-root" href="{{url}}" data-collapsible="navPages-{{id}}">
        {{name}} <i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
    </a>
    {{> components/common/navigation-dropdown}}
{{else}}
    <a class="navPages-action" href="{{url}}">{{name}}</a>
{{/if}}

components/products/price:
{{#or has_options product.options.length}}
    {{#if price.with_tax}}
        <div class="price-section price-section--withTax" {{#if schema_org}}itemprop="offers" itemscope itemtype="http://schema.org/Offer"{{/if}}>
            {{#if price.rrp_with_tax}}
                <span data-product-rrp-with-tax class="price price--rrp">{{price.rrp_with_tax.formatted}}</span>
            {{/if}}
            {{#if page_type '!==' 'product'}}
                {{#if theme_settings.price_as_low_as}}
                    <span translate>As low as </span>
                {{/if}}
            {{/if}}
            <span data-product-price-with-tax class="price">{{price.with_tax.formatted}}</span>
            {{#if price.without_tax}}
                {{lang 'products.price_with_tax' tax_label=price.tax_label}}
            {{/if}}
        </div>
    {{/if}}
    {{#if price.without_tax}}
        <div class="price-section price-section--withoutTax {{#if price.with_tax}}price-section--minor{{/if}}"  {{#if schema_org}}itemprop="offers" itemscope itemtype="http://schema.org/Offer"{{/if}}>
            {{#if price.rrp_without_tax}}
                <span data-product-rrp-without-tax class="price price--rrp">{{price.rrp_without_tax.formatted}}</span>
            {{/if}}
            {{#if schema_org}}
                <meta itemprop="price" content="{{price.without_tax.value}}">
                <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
                <meta itemprop="availability" content="{{product.availability}}">
                <meta itemprop="itemCondition" itemtype="http://schema.org/OfferItemCondition" content="http://schema.org/{{product.condition}}Condition">
            {{/if}}
            {{#if page_type '!==' 'product'}}
                {{#if theme_settings.price_as_low_as}}
                    <span translate>As low as </span>
                {{/if}}
            {{/if}}
            <span data-product-price-without-tax class="price price--withoutTax">{{price.without_tax.formatted}}</span>
            {{#if price.with_tax}}
                <abbr title="{{lang 'products.excluding_tax'}}">{{lang 'products.price_without_tax' tax_label=price.tax_label}}</abbr>
            {{/if}}
        </div>
    {{/if}}
    {{#if page_type '===' 'product'}}
        {{#if price.saved}}
            <div class="price-section price-section--saving">
                <span class="price">
                    {{lang 'products.you_save' amount=price.saved.formatted}}
                </span>
            </div>
        {{/if}}
    {{/if}}
{{else}}
    {{#if price.with_tax}}
        <div class="price-section price-section--withTax" {{#if schema_org}}itemprop="offers" itemscope itemtype="http://schema.org/Offer"{{/if}}>
            {{#if price.rrp_with_tax}}
                <span data-product-rrp-with-tax class="price price--rrp">{{price.rrp_with_tax.formatted}}</span>
            {{/if}}
            <span data-product-price-with-tax class="price">{{price.with_tax.formatted}}</span>
            {{#if price.without_tax}}
                {{lang 'products.price_with_tax' tax_label=price.tax_label}}
            {{/if}}
        </div>
    {{/if}}
    {{#if price.without_tax}}
        <div class="price-section price-section--withoutTax {{#if price.with_tax}}price-section--minor{{/if}}"  {{#if schema_org}}itemprop="offers" itemscope itemtype="http://schema.org/Offer"{{/if}}>
            {{#if price.rrp_without_tax}}
                <span data-product-rrp-without-tax class="price price--rrp">{{price.rrp_without_tax.formatted}}</span>
            {{/if}}
            {{#if schema_org}}
                <meta itemprop="price" content="{{price.without_tax.value}}">
                <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
            {{/if}}

            <span data-product-price-without-tax class="price price--withoutTax{{#if stock_level '==' 0}} price--stroke{{/if}}">{{price.without_tax.formatted}}</span>

            {{#if stock_level '==' 0}}
                <span class="price"> SOLD OUT</span>
            {{/if}}

            {{#if price.with_tax}}
                <abbr title="{{lang 'products.excluding_tax'}}">{{lang 'products.price_without_tax' tax_label=price.tax_label}}</abbr>
            {{/if}}
        </div>
    {{/if}}
    {{#if page_type '===' 'product'}}
        {{#if price.saved}}
            <div class="price-section price-section--saving">
                <span class="price">
                    {{lang 'products.you_save' amount=price.saved.formatted}}
                </span>
            </div>
        {{/if}}
    {{/if}}
{{/or}}

components/products/ratings:
{{#for 1 5}}
    {{#if ../this.rating '>=' $index}}
        <span class="icon icon--ratingFull">
            <svg>
                <use xlink:href="#icon-star" />
            </svg>
        </span>
    {{else}}
        <span class="icon icon--ratingEmpty">
            <svg>
                <use xlink:href="#icon-star" />
            </svg>
        </span>
    {{/if}}
{{/for}}
{{{snippet 'product_rating'}}}
