.CartContents {
    font-family:         PTMono-Bold;
    font-size:           14px;
    font-weight:         bold;
    text-transform:      uppercase;
    letter-spacing:      1px;
    color:               #000000;
    background-image:    url('../img/cart_checkout/stamp-circle-bons.png');
    background-repeat:   no-repeat;
    background-position: 1% 40%;

    em.ProductPrice {
        font-style: normal;
    }

    thead {
        background-color: #fbf1f0;
        // background-color: hsla(5, 58%, 96%, 0.3);

        tr { border-bottom: dashed; }
    }

    tbody {
        background-color: #fbf1f0;
        // background-color: hsla(5, 58%, 96%, 0.3);

        @media (min-width: 551px) {
            border-bottom: dashed;
        }

        tr {
            td {
                border-bottom: none;
            }

            @media (max-width: 551px) {
                display:        flex;
                flex-direction: column;
                border-bottom:  dashed 3px black !important;

                & > td {
                    width: 100% !important;
                }

                & > td:not(:first-child) {
                    display:    block;
                    position:   relative;
                    text-align: right !important;
                }

                & > td:nth-child(2)::before {
                    content:     'QUANTITY';
                    display:     block;
                    position:    absolute;
                    left:        8px;
                    top:         8px;
                    font-weight: normal;
                }

                & > td:nth-child(3)::before {
                    content:     'ITEM PRICE';
                    display:     block;
                    position:    absolute;
                    left:        8px;
                    top:         8px;
                    font-weight: normal;
                }

                & > td:nth-child(4)::before {
                    content:     'ITEM TOTAL';
                    display:     block;
                    position:    absolute;
                    left:        8px;
                    top:         8px;
                    font-weight: normal;
                }

                & > td:last-child {
                    padding-bottom: 30px;
                }
            }
        }
    }

    tfoot {
        .Textbox.Field200 {
            margin-bottom: 0 !important;
        }

        .SubTotal {
            @media (max-width: 551px) {
                display:         flex;
                justify-content: space-between;
                border-top:      none !important;
            }

            td {
                border-top:    none;
                border-bottom: none;

                strong {
                    font-weight: normal;
                }

                div {
                    padding: 12px 0;
                }
            }
        }

        .CheckoutRedeemCoupon {
            background-color: hsla(0, 0%, 100%, 0.3);

            @media (max-width: 551px) {
                label { line-height: 1rem; }

                .Textbox.Field200 {
                    margin-bottom: 1rem !important;
                }
            }
        }

        & > tr:last-child {
            @media (max-width: 551px) {
                & > td:first-child {
                    margin-left:  auto;
                    margin-right: auto;
                }
            }

            strong {
                margin-top: 5px;
            }

            em:last-of-type {
                font-family:    PTMono-Bold;
