#square-cc-form {
    display:  block;
    position: relative;

    &::before {
        content:           '';
        display:           block;
        width:             205px;
        height:            202px;
        position:          absolute;
        bottom:            -60px;
        left:              15%;
        background-image:  url('../img/cart_checkout/stamp-circle-bons.png');
        background-repeat: no-repeat;
        pointer-events:    none;

        @media (max-width: 551px) {
            bottom: 40px;
            left:   -118px;
        }
    }

    dl {
        display:               grid;
        grid-template-columns: repeat(3, 1fr);
        grid-column-gap:       20px;
        grid-row-gap:          16px;
        width:                 100%;

        @media (max-width: 551px) {
            display: block;
        }

        dt,
        dd {
            width:          100%;
            text-align:     left;
            margin-left:    0 !important;
        }

        dd input {
            height:           50px !important;
            margin-bottom:    30px !important;
            border-radius:    0 !important;
            border:           none !important;
            font-family:      PTMono-Bold !important;
            font-size:        18px !important;
            letter-spacing:   0 !important;
            background-color: #fbf1f0 !important;
            color:            #000000;

            &:focus {
                border: 3px solid #E9B3B3 !important;
            }
        }

        // Credit card Number
        & > dt:first-of-type {
            grid-column-start: 1;
            grid-column-end: 4;
            grid-row-start: 1;
            grid-row-end: 2;
            -ms-grid-column-span: 3;
        }

        & > dd:first-of-type {
            grid-column-start: 1;
            grid-column-end: 4;
            grid-row-start: 2;
            grid-row-end: 3;
            -ms-grid-column-span: 3;
        }

        //Expiration Date
        & > dt:nth-of-type(2) {
            grid-column-start: 1;
            grid-column-end: 2;
            grid-row-start: 3;
            grid-row-end: 4;
        }

        & > dd:nth-of-type(2) {
            grid-column-start: 1;
            grid-column-end: 2;
            grid-row-start: 4;
            grid-row-end: 5;
        }


        // CVV2
        & > dt:nth-of-type(3) {
            grid-column-start: 2;
            grid-column-end: 3;
            grid-row-start: 3;
            grid-row-end: 4;
        }

        & > dd:nth-of-type(3) {
            grid-column-start: 2;
            grid-column-end: 3;
            grid-row-start: 4;
            grid-row-end: 5;
        }


        // Postal Code
        & > dt:nth-of-type(4) {
            grid-column-start: 3;
            grid-column-end:   4;
            grid-row-start:    3;
            grid-row-end:      4;
        }

        & > dd:nth-of-type(4) {
            grid-column-start: 3;
            grid-column-end: 4;
            grid-row-start: 4;
            grid-row-end: 5;
        }


        // Total
        & > dt:nth-of-type(5) {
            margin-top:        3rem;
            font-family:       PTMono-Bold;
            font-size:         14px;
            font-weight:       bold;
            letter-spacing:    3px;
            color:             #000000;

            grid-column-start: 1;
            grid-column-end:   2;
            grid-row-start:    5;
            grid-row-end:      6;

            @media (min-width: 551px) {
                text-align: right !important;
            }

            @media (max-width: 551px) {
                text-align: left !important;
            }
        }

        & > dd:nth-of-type(5) {
            font-family:    PTMono-Bold;
            font-size:      50px;
            font-weight:    bold;
            letter-spacing: -2px;
            line-height:    36px;
            color:          #000000;

            grid-column-start: 2;
            grid-column-end:   4;
            grid-row-start:    5;
            grid-row-end:      6;
            -ms-grid-column-span: 2;

            @media (min-width: 551px) {
                margin-top: 3rem;
            }

            @media (max-width: 551px) {
                margin-bottom:  3rem;
                font-size:      32px;
                letter-spacing: -1.28px;
            }

            em {
                font-style: normal;
            }
        }
    }

    .Submit {
        display:  block;
        position: relative;

        @media (min-width: 551px) {
            float: right;

            &::before {
                content:           '';
                display:           block;
                width:             84px;
                height:            62px;
                position:          absolute;
                top:               0;
                left:              -90px;
                background-image:  url('../img/cart_checkout/stamp-arrow-right.png');
                background-repeat: no-repeat;
            }

            &::after {
                content:           '';
                display:           block;
                width:             84px;
